import React, { useEffect, useState } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import data from "./geojson.json";
var custom_map = null;

const Map = () => {
  // const [map, setMap] = useState(null);

  const space = " = ";
  const addGeoJson = () => {
    let layer = L.geoJSON(data, {
      onEachFeature: function (feature, layer) {
        layer.addEventListener("click", () => {
          layer.bindPopup(feature.properties.name + space + feature.properties.pohon);
        }); 
      }
    });
    layer.addTo(custom_map); 
  };


  useEffect(() => {
    custom_map = L.map("mapid").setView([-8.088, 113.626], 13);
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution:'PDP Kahyangan Jember'
    }).addTo(custom_map);
  
    addGeoJson();
  }, []);
  return (
    <div>
      <div
        id="mapid"
        style={{
          width: "100%",
          height: "90vh" || window.screen.availHeight
        }}
      ></div>
      <div>
      <div class="table-responsive">
  <table class="table table-striped table-bordered table-sm ">
    <thead>
      <tr>
        <th scope="col"></th>
        <th scope="col">Luas Area</th>
        <th scope="col">Jumlah Pohon</th>
       
      </tr>
    </thead>
    <tbody>
      <tr>
        <th scope="row">*</th>
        <td>

        const rootElement = document.getElementById("root");
        </td>
        <td>0</td>
       
      </tr>
     
    </tbody>

    <caption>
  
    </caption>

  </table>
</div>
      </div>
    </div>
  );
};


export default Map;
