import React, { useEffect, useState } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import data from "./geojson.json";
import {  Marker } from 'leaflet';
var custom_map = null;

function MyMarkers ({markers}) {
  // check length of the markers
 if (markers.length > 0){
   // iterate through the array of markers creating a marker with unique id at given latlngs
   markers.map((position, id) => { 
     return <Marker key={id} position={position}/>
   })    

} else {
 return null;
}
};

const Map = () => {
  // const [map, setMap] = useState(null);

  const space = " = ";
  const addGeoJson = () => {
    let layer = L.geoJSON(data, {
      onEachFeature: function (feature, layer) {
        layer.addEventListener("click", () => {
          layer.bindPopup(feature.properties.name + space + feature.properties.pohon);
        }); 
      }
    });
    layer.addTo(custom_map);
  };


  useEffect(() => {
    custom_map = L.map("mapid").setView([-8.088, 113.626], 13);
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution:'PDP Kahyangan Jember'
    }).addTo(custom_map);
  
    addGeoJson();
  }, []);
  return (
    <div>
      <div
        id="mapid"
        style={{
          width: "100%",
          height: "90vh" || window.screen.availHeight
        }}
      ></div>
    </div>
  );
};


export default Map;
